mapper.sql:
FROM company a ,
( SELECT  distinct company_id as company_id FROM company_category


		AND category_id = #{categoryId}


) b , expo_company c


	a.id=b.company_id

	and a.id = c.company_id

	and c.expo_id = #{expoId}


		AND a.name LIKE concat(concat('%',#{name}),'%')



			AND a.seq < #{lastSeq}
